--PROCEDURE =========================================================================================================================================IF  EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].[sp_rel_listagem_de_precos]') AND type in (N'P', N'PC'))begin	DROP PROCEDURE sp_rel_listagem_de_precosendgo create procedure sp_rel_listagem_de_precos	@FILIAL VARCHAR(20),	@plu  varchar(20),	@ean  varchar(20),	@descricao varchar(50),	@referencia varchar(20)	asSELECT	m.REF_Fornecedor AS REF, m.PLU, EAN = ISNULL(ean.EAN, ''), NCM = ISNULL(m.CF, ''), m.descricao, l.Preco AS Venda	FROM Mercadoria m INNER JOIN Mercadoria_Loja l ON m.PLU = l.PLU LEFT OUTER JOIN EAN ON ean.PLU = m.PLU 	WHERE ISNULL(l.Preco, 0) > 0	and (LEN(@plu)=0 or l.PLU=@plu)	and (LEN(@ean)=0 or ean.EAN=@ean)	and (LEN(@descricao)=0 or m.descricao like @descricao+'%')	and (LEN(@referencia)=0 or m.Ref_fornecedor = @referencia)	AND L.Filial = @FILIAL		go --PROCEDURE =========================================================================================================================================IF  EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].[sp_rel_listagem_de_precos_custo_margem]') AND type in (N'P', N'PC'))begin	DROP PROCEDURE sp_rel_listagem_de_precos_custo_margemendgo create procedure sp_rel_listagem_de_precos_custo_margem	@FILIAL VARCHAR(20),	@plu  varchar(20),	@ean  varchar(20),	@descricao varchar(50),	@referencia varchar(20)	as  SELECT    m.REF_Fornecedor AS REF, m.PLU, EAN = ISNULL(ean.EAN, ''), NCM = ISNULL(m.CF, ''), m.descricao, l.Preco_Custo as Custo, Convert(Decimal(7, 2),l.Margem) as Mrg, l.Preco AS Venda    FROM Mercadoria m INNER JOIN Mercadoria_Loja l ON m.PLU = l.PLU LEFT OUTER JOIN EAN ON ean.PLU = m.PLU    WHERE ISNULL(l.Preco, 0) > 0	and (LEN(@plu)=0 or l.PLU=@plu)	and (LEN(@ean)=0 or ean.EAN=@ean)	and (LEN(@descricao)=0 or m.descricao like @descricao+'%')	and (LEN(@referencia)=0 or 