--PROCEDURES =======================================================================================
CREATE  PROCEDURE SP_CEST_NCM (  @ncm  VARCHAR(10) )
AS
BEGIN

Declare creCEST cursor for  Select convert(int ,LEN(ncm)) as tam from cest where LEN(ncm)>0 AND LEN(ncm)< 8   group by LEN(ncm)
declare @SqlString Nvarchar(max);
declare @sqlWhere nvarchar(max);
SET @SqlString = '';
set @sqlWhere = '';
DECLARE @TAMCARC int = 0;
open creCEST
FETCH NEXT FROM creCEST INTO @TAMCARC ;
WHILE @@FETCH_STATUS = 0
   BEGIN
	
		SET  @sqlWhere = @sqlWhere + ' OR (NCM = '+ CHAR(39) +SUBSTRING(@ncm,0,(@TAMCARC+1))+ CHAR(39) +')';
	  --SET @SqlString = @SqlString + CONVERT(VARCHAR(10),@TRIB)
		 
      FETCH  NEXT FROM creCEST INTO @TAMCARC;
      
   END;

CLOSE creCEST;
DEALLOCATE creCEST;





SET @SqlString = 'Select CEST, DESCRICAO  FROM CEST WHERE (NCM ='+ CHAR(39) +@NCM+ CHAR(39) +')' +@sqlWhere +' group by CEST,descricao';


exec (@SqlString);
END